mapper.select:
select ifnull(count(*), 0)
from itemlike
where id=#{id} and seq=#{seq}

mapper.update:
update item
set likecount=likecount+1
where seq=#{seq}

update item
set likecount=likecount-1
where seq=#{seq}

update item
set title=#{title}, content=#{content}, location=#{location}, price=#{price}, itemcondition=#{itemcondition}, 
filename=#{filename}, newfilename=#{newfilename}
where seq=#{seq}
